mapper.select:
SELECT a.*,b.name as industry_name, (SELECT count(1) FROM (SELECT a.course_id from exam_train_course_material as a INNER JOIN exam_train_material as b on a.material_id = b.id where b.content_type = 1) as b where b.course_id = a.id ) as video_material_number, (SELECT count(1) FROM exam_train_course_material WHERE course_id = a.id) as material_number , (SELECT SUM(b.score) FROM (SELECT a.course_id,b.score from exam_train_course_material as a INNER JOIN exam_train_material as b on a.material_id = b.id) as b where b.course_id = a.id GROUP BY b.course_id ) as course_score FROM (SELECT a.*,b.name as work_type_name FROM exam_train_course as a LEFT JOIN sys_work_type as b on a.worktype=b.id
a.name like CONCAT('%',#{examTrainMaterialSearch.courseName},'%') AND a.worktype = #{examTrainMaterialSearch.workType} AND a.industry = #{examTrainMaterialSearch.industryCategory}
order by a.id desc limit #{page.offset}, #{page.pageSize})as a LEFT JOIN sys_industry as b on a.industry=b.id

SELECT count(1)
FROM exam_train_course
WHERE name = #{name}
