mapper.insert:

insert into exam_train_material (id, name, industry,
work_type, score, content_type,
remark, operator, operate_time,
operate_ip, picture, content
)
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{industry,jdbcType=INTEGER},
#{workType,jdbcType=INTEGER}, #{score,jdbcType=REAL}, #{contentType,jdbcType=INTEGER},
#{remark,jdbcType=VARCHAR}, #{operator,jdbcType=VARCHAR}, #{operateTime,jdbcType=TIMESTAMP},
#{operateIp,jdbcType=VARCHAR}, #{picture,jdbcType=LONGVARCHAR}, #{content,jdbcType=LONGVARCHAR}
)

mapper.select:
SELECT count(1)
FROM exam_train_material
WHERE content_type = #{contentType}

SELECT a.*,b.name as industry_name FROM
(SELECT a.*,b.name as work_type_name  FROM exam_train_material as a LEFT JOIN sys_work_type as b on a.work_type=b.id

  a.name like CONCAT('%',#{examTrainMaterialSearch.name},'%')
  AND a.work_type = #{examTrainMaterialSearch.workType}
  AND a.industry = #{examTrainMaterialSearch.industryCategory}
  AND a.operator = #{examTrainMaterialSearch.operator}
  AND a.content_type = #{examTrainMaterialSearch.typeId} 

)as a LEFT JOIN
sys_industry as b on a.industry=b.id
limit #{page.offset}, #{page.pageSize}

SELECT count(1)
FROM exam_train_material
WHERE name = #{name}

SELECT a.*,b.completion_rate as completion_rate,b.start_time as start_time,b.end_time as end_time,
a.score as all_period, (a.score*b.completion_rate) as now_period
from  exam_train_material as a
LEFT JOIN exam_train_ca_material as b on a.id = b.train_material_id and b.ca_id = #{caId} and b.course_id = #{courseId}
WHERE a.id IN

  #{id}
