WHERE  a.area IN

  #{area}


AND a.enterprise_name LIKE CONCAT('%',#{search.enterprise},'%')
AND a.record_no LIKE CONCAT('%',#{search.recordNo},'%')
  AND a.record_date >= #{search.start}
 AND a.record_date <= #{search.end} 
AND publicity = #{search.publicity}

order by id desc
limit #{page.offset}, #{page.pageSize}

