SELECT count(1)
FROM (select a.*,b.enterprise_name as enterprise_name FROM (select a.*,b.name as area_name FROM format_dishes as a LEFT JOIN
sys_area as b on a.area=b.id) as a LEFT JOIN supervision_enterprise as b on a.unit = b.id) as a
WHERE  area IN

  #{area}


AND a.name = #{search.name}
AND a.number = #{search.number}
AND a.type = #{search.type}
AND a.price = #{search.price}

