SELECT count(1)
FROM (select a.*,b.enterprise_name as enterprise_name FROM (select a.*,b.name as area_name FROM format_disinfection as a LEFT JOIN
sys_area as b on a.area=b.id) as a LEFT JOIN supervision_enterprise as b on a.unit = b.id) as a
WHERE  area IN

  #{area}


AND a.enterprise_name LIKE CONCAT('%',#{search.enterprise},'%')
AND a.name LIKE CONCAT('%',#{search.name},'%')
AND a.amount = #{search.amount}
AND a.person = #{search.person}
  AND a.date >= #{search.start}
 AND a.date <= #{search.end}

