mapper.select:
SELECT * FROM (select a.*,b.enterprise_name as enterprise_name FROM (select a.*,b.name as area_name FROM format_goods as a LEFT JOIN sys_area as b on a.area=b.id) as a LEFT JOIN supervision_enterprise as b on a.unit = b.id) as a WHERE a.area IN
#{area}
AND a.enterprise_name LIKE CONCAT('%',#{search.enterprise},'%') AND a.name LIKE CONCAT('%',#{search.name},'%') AND a.supplier LIKE CONCAT('%',#{search.supplier},'%') AND a.type LIKE CONCAT('%',#{search.type},'%') AND a.day LIKE CONCAT('%',#{search.day},'%') AND a.time >= #{search.start1} AND a.time <= #{search.end1}
limit #{page.offset}, #{page.pageSize}

SELECT 
FROM format_goods
where unit=#{enterprise}

AND enterprise_name LIKE CONCAT('%',#{search.enterprise},'%')
AND name LIKE CONCAT('%',#{search.name},'%')
AND supplier LIKE CONCAT('%',#{search.supplier},'%')
AND type LIKE CONCAT('%',#{search.type},'%')
AND day LIKE CONCAT('%',#{search.day},'%')
  AND time >= #{search.start1}
 AND time <= #{search.end1} 

limit #{page.offset}, #{page.pageSize}

SELECT count(1)
FROM format_goods
WHERE unit=#{Unit} AND type = #{Type} AND name = #{Name} AND supplier = #{Supplier} AND brand = #{Brand}

  AND id != #{id}

