SELECT count(1) FROM (select a.*,b.enterprise_name as enterprise_name FROM (select a.*,b.name as area_name FROM format_origin_record_ex as a LEFT JOIN sys_area as b on a.area=b.id) as a LEFT JOIN supervision_enterprise as b on a.enterprise = b.id)as a WHERE a.area IN
#{area}
AND a.enterprise_name LIKE CONCAT('%',#{search.enterprise},'%') AND a.area_name LIKE CONCAT('%',#{search.area},'%') AND a.origin_type LIKE CONCAT('%',#{search.type},'%') AND a.origin_name LIKE CONCAT('%',#{search.name},'%') AND a.record_time >= #{search.start1} AND a.record_time <= #{search.end1}

SELECT * FROM (select a.*,b.enterprise_name as enterprise_name FROM (select a.*,b.name as area_name FROM format_origin_record_ex as a LEFT JOIN sys_area as b on a.area=b.id) as a LEFT JOIN supervision_enterprise as b on a.enterprise = b.id)as a WHERE a.area IN
#{area}
AND a.enterprise_name LIKE CONCAT('%',#{search.enterprise},'%') AND a.area_name LIKE CONCAT('%',#{search.area},'%') AND a.origin_type LIKE CONCAT('%',#{search.type},'%') AND a.origin_name LIKE CONCAT('%',#{search.name},'%') AND a.record_time >= #{search.start1} AND a.record_time <= #{search.end1} order by id desc limit #{page.offset}, #{page.pageSize}

SELECT count(1)
FROM
(select a.*,b.enterprise_name as enterprise_name FROM (select a.*,b.name as area_name FROM format_origin_record_ex as a LEFT JOIN
sys_area as b on a.area=b.id) as a LEFT JOIN supervision_enterprise as b on a.enterprise = b.id)as  a
where a.enterprise=#{enterprise}

AND a.origin_type LIKE CONCAT('%',#{search.type},'%')
AND a.origin_name LIKE CONCAT('%',#{search.name},'%')
  AND a.record_time >= #{search.start1}
 AND a.record_time <= #{search.end1}

SELECT
*
FROM
(select a.*,b.enterprise_name as enterprise_name FROM (select a.*,b.name as area_name FROM format_origin_record_ex as a LEFT JOIN
sys_area as b on a.area=b.id) as a LEFT JOIN supervision_enterprise as b on a.enterprise = b.id)as  a
where a.enterprise=#{enterprise}

AND a.origin_type LIKE CONCAT('%',#{search.type},'%')
AND a.origin_name LIKE CONCAT('%',#{search.name},'%')
  AND a.record_time >= #{search.start1}
 AND a.record_time <= #{search.end1} 
order by id desc
limit #{page.offset}, #{page.pageSize}

SELECT a.*,b.bill_list FROM format_origin_record_ex as a
LEFT JOIN
(SELECT a.record_id,GROUP_CONCAT(a.bill_id) bill_list FROM origin_record_bill
as a GROUP BY a.record_id) as b
on a.id=b.record_id where a.enterprise = #{enterpriseId}
    AND record_time >= #{startDate}
   AND record_time < #{endDate}

SELECT
*
FROM format_origin_record_ex
