SELECT count(1)
FROM (select a.*,b.enterprise_name as enterprise_name FROM (select a.*,b.name as area_name FROM format_origin_record as a LEFT JOIN
sys_area as b on a.area=b.id) as a LEFT JOIN supervision_enterprise as b on a.enterprise = b.id)as  a
WHERE  a.area IN

  #{area}


AND a.enterprise_name LIKE CONCAT('%',#{search.enterprise},'%')
  AND a.record_time >= #{search.start}
 AND a.record_time <= #{search.end}

