SELECT 
FROM format_supplier
where  enterprise_id=#{eid}

and stype = #{search.stype}
and name LIKE CONCAT('%',#{search.name},'%')
and number LIKE CONCAT('%',#{search.number},'%')
and address LIKE CONCAT('%',#{search.address},'%')
order by id desc
limit #{page.offset}, #{page.pageSize}

