*
FROM
(select a.*,b.enterprise_name as enterprise_name FROM (select a.*,b.name as area_name FROM format_waste as a LEFT JOIN
sys_area as b on a.area=b.id) as a LEFT JOIN supervision_enterprise as b on a.enterprise = b.id) as a
WHERE  a.area IN

  #{area}


AND a.enterprise_name LIKE CONCAT('%',#{search.enterprise},'%')
AND a.kind = #{search.kind}
AND a.disposalperson = #{search.person}
  AND a.disposaltime >= #{search.start1}
 AND a.disposaltime <= #{search.end1} 
  AND a.registrationtime >= #{search.start2}
 AND a.registrationtime <= #{search.end2} 
order by id desc
limit #{page.offset}, #{page.pageSize}

