SELECT a.*,b.name as ga_unit FROM (SELECT a.*,b.name as dept_name FROM ((SELECT a.*,b.name as ga_name,b.enforce as ga_enfore,b.mobile_phone as ga_phone,b.department,b.job FROM inspect_sign_conf as a INNER JOIN supervision_ga as b on a.ga_id = b.id
AND b.id IN #{ids}
AND b.name LIKE CONCAT('%',#{searchParam.gaName},'%') AND b.department = #{searchParam.deptId}) union (SELECT a.*,b.name as ga_name,b.enforce as ga_enfore,b.mobile_phone as ga_phone,b.department,b.job FROM inspect_sign_conf as a INNER JOIN supervision_ga as b on a.ga_id = b.id
AND b.id NOT IN #{ids}
AND b.name LIKE CONCAT('%',#{searchParam.gaName},'%') AND b.department = #{searchParam.deptId}) )as a LEFT JOIN sys_dept as b on a.department = b.id) as a LEFT JOIN sys_duties_info as b on a.job = b.id limit #{page.offset}, #{page.pageSize}

SELECT count(1) FROM ((SELECT a.*,b.name as ga_name,b.enforce as ga_enfore,b.mobile_phone as ga_phone,b.department,b.job FROM inspect_sign_conf as a INNER JOIN supervision_ga as b on a.ga_id = b.id
AND b.id IN #{ids}
AND b.name LIKE CONCAT('%',#{searchParam.gaName},'%') AND b.department = #{searchParam.deptId}) union (SELECT a.*,b.name as ga_name,b.enforce as ga_enfore,b.mobile_phone as ga_phone,b.department,b.job FROM inspect_sign_conf as a INNER JOIN supervision_ga as b on a.ga_id = b.id
AND b.id NOT IN #{ids}
AND b.name LIKE CONCAT('%',#{searchParam.gaName},'%') AND b.department = #{searchParam.deptId}) ) as a

SELECT count(1)
FROM inspect_sign_conf
WHERE ga_id = #{gaId}

  AND id != #{id}
