mapper.select:
SELECT
*
FROM quick_check_base
WHERE  team = #{team} and state = '已上报'

AND check_company LIKE CONCAT('%',#{search.company},'%')
AND market LIKE CONCAT('%',#{search.market},'%')
  AND date >= #{search.start1}
 AND date <= #{search.end1} 
order by date desc
limit #{page.offset}, #{page.pageSize}

SELECT count(1)
FROM quick_check_base
WHERE  market = #{market}

AND check_company LIKE CONCAT('%',#{search.company},'%')
AND market LIKE CONCAT('%',#{search.team},'%')
  AND date >= #{search.start1}
 AND date <= #{search.end1}

SELECT
*
FROM quick_check_base
WHERE  market = #{market}

AND check_company LIKE CONCAT('%',#{search.company},'%')
AND team LIKE CONCAT('%',#{search.team},'%')
  AND date >= #{search.start1}
 AND date <= #{search.end1} 
order by date desc
limit #{page.offset}, #{page.pageSize}

SELECT
*
FROM quick_check_base
WHERE  1=1

AND check_company LIKE CONCAT('%',#{search.company},'%')
AND market LIKE CONCAT('%',#{search.market},'%')
  AND date >= #{search.start1}
 AND date <= #{search.end1} 
order by date desc
limit #{page.offset}, #{page.pageSize}

mapper.update:
update quick_check_base
set  state = '已上报'
where id = #{id}
