mapper.select:
SELECT * FROM spot_check_base WHERE team = #{team} and state = '已上报'
AND check_company LIKE CONCAT('%',#{search.company},'%') AND sample_name LIKE CONCAT('%',#{search.name},'%') AND date >= #{search.start1} AND date <= #{search.end1} AND check_result LIKE CONCAT('%',#{search.result},'%') AND sample_person LIKE CONCAT('%',#{search.person},'%') AND check_orginization LIKE CONCAT('%',#{search.orginization},'%')
order by date desc limit #{page.offset}, #{page.pageSize}

SELECT count(1)
FROM spot_check_base
WHERE  check_orginization = #{org}

AND check_company LIKE CONCAT('%',#{search.company},'%')
AND sample_name LIKE CONCAT('%',#{search.name},'%')
  AND date >= #{search.start1}
 AND date <= #{search.end1} 
AND check_result LIKE CONCAT('%',#{search.result},'%')
AND sample_person LIKE CONCAT('%',#{search.person},'%')

AND team LIKE CONCAT('%',#{search.team},'%')

SELECT
*
FROM spot_check_base
WHERE  check_orginization = #{org}

AND check_company LIKE CONCAT('%',#{search.company},'%')
AND sample_name LIKE CONCAT('%',#{search.name},'%')
  AND date >= #{search.start1}
 AND date <= #{search.end1} 
AND check_result LIKE CONCAT('%',#{search.result},'%')
AND sample_person LIKE CONCAT('%',#{search.person},'%')

AND team LIKE CONCAT('%',#{search.team},'%')
order by date desc
limit #{page.offset}, #{page.pageSize}

SELECT * FROM spot_check_base WHERE 1=1
AND check_company LIKE CONCAT('%',#{search.company},'%') AND sample_name LIKE CONCAT('%',#{search.name},'%') AND date >= #{search.start1} AND date <= #{search.end1} AND check_result LIKE CONCAT('%',#{search.result},'%') AND sample_person LIKE CONCAT('%',#{search.person},'%') AND check_orginization LIKE CONCAT('%',#{search.orginization},'%')
order by date desc limit #{page.offset}, #{page.pageSize}

