mapper.select:
SELECT count(1) FROM (select a.*,b.name as area_name FROM spot_check_enterprise as a LEFT JOIN sys_area as b on a.area=b.id) as a WHERE a.area IN
#{area}
AND a.enterprise_name LIKE CONCAT('%',#{search.enterprise},'%') AND a.enterprise_nickname LIKE CONCAT('%',#{search.nickname},'%')

SELECT * FROM (select a.*,b.name as area_name FROM spot_check_enterprise as a LEFT JOIN sys_area as b on a.area=b.id) as a WHERE a.area IN
#{area}
AND a.enterprise_name LIKE CONCAT('%',#{search.enterprise},'%') AND a.enterprise_nickname LIKE CONCAT('%',#{search.nickname},'%') order by id desc limit #{page.offset}, #{page.pageSize}

mapper.update:
update spot_check_enterprise
set  state = 1
where id = #{id}
