#{area}
AND a.enterprise_name LIKE CONCAT('%',#{search.enterpriseName},'%') AND a.area_name LIKE CONCAT('%',#{search.areaName},'%')
limit #{page.offset}, #{page.pageSize}

SELECT * FROM (select a.*,b.name as area_name from (SELECT a.*,b.enterprise_name as enterprise_name,b.area as area, b.registered_address as address, b.id_number as permission_id,b.year_assessment as check_level, b.cantact_way as contact,b.cantacts as charger from (SELECT DISTINCT enterprise_id,count(1) as record_count FROM video_record where type =1 GROUP BY enterprise_id)as a left join supervision_enterprise as b on a.enterprise_id = b.id)as a left join sys_area as b on a.area = b.id) as a WHERE 1=1
AND a.enterprise_name LIKE CONCAT('%',#{search.enterpriseName},'%') AND a.area_name LIKE CONCAT('%',#{search.areaName},'%')
limit #{page.offset}, #{page.pageSize}

